COMMANDS.COMMAND:
	buf = new StringBuffer();
	if(exec_rvm)
	  buf.append("rvm " + exec_rvm_environment + " do ");

	if(bundled)
	  buf.append(" bundle exec ");

	if(run_pry)
    return(" pry ");

	buf.append("ruby \"" + scriptname + "\"");

	if(!args.equals(""))
		buf.append(" "+args);

	// return value
	return buf.toString();
